import logo from "./logo.svg";
import "./App.css";
import axios from "axios";
import FormControl from "react-bootstrap/FormControl";
import { Form, Button, Col, Row, Card } from "react-bootstrap";
import { useEffect, useState } from "react";

function App() {
  const [userdata, setUserdata] = useState(null);
  const [firstName, setfirstName] = useState("");
  const [lastName, setlastName] = useState("");
  const [mobile, setmobile] = useState("");
  const [email, setemail] = useState("");

  const saveNewuser = () => {
    let newUser = {
      firstName: firstName,
      lastName: lastName,
      mobile: mobile,
      email: email
    }
    axios.post("http://localhost:5000/api/user/userModel", newUser)
  }

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/user/getdata")
      .then((res) => setUserdata(res.data))
      .catch((err) => console.log(err));
  }, []);
  return (
    <div className="App">
      <Card style={{width:"50rem"}}>
        <Card.Header>
          New User Register
        </Card.Header>
        <Card.Body>
          <Form>
            <Row style={{textAlign:"center"}}>
              <Col md={4}>
                <FormControl placeholder="First Name" type="text" onChange={(e) => setfirstName(e.target.value)}/>
              </Col>
              <Col md={4}>
                <FormControl placeholder="Last Name" type="text" onChange={(e) => setlastName(e.target.value)}/>
              </Col>
            </Row>
            <br />
            <Row style={{textAlign:"center"}}>
              <Col md={4}>
                <FormControl placeholder="Email" type="email" onChange={(e) => setemail(e.target.value)}/>
              </Col>
              <Col md={4}>
                <FormControl placeholder="Mobile Number" type="number" onChange={(e) => setmobile(e.target.value)}/>
              </Col>
            </Row>
            <br />
            <Button variant="primary" type="submit" onClick={saveNewuser}>
              Submit
            </Button>
          </Form>
        </Card.Body>
      </Card>
    </div>
  );
}

export default App;
